package uz.lutfullayevmuhammad.lesson63.core.di.module.networkimport dagger.Moduleimport dagger.Providesimport retrofit2.Retrofitimport uz.lutfullayevmuhammad.lesson63.core.api.SpoonacularApiimport javax.inject.Singleton@Moduleclass ApiModule {    @Provides    @Singleton    fun provideSpoonApi(retrofit: Retrofit): SpoonacularApi {        return retrofit.create(SpoonacularApi::class.java)    }}