package uz.lutfullayevmuhammad.lesson63.ui.detailsimport android.util.Logimport androidx.lifecycle.MutableLiveDataimport androidx.lifecycle.ViewModelimport kotlinx.coroutines.CoroutineScopeimport kotlinx.coroutines.Dispatchersimport kotlinx.coroutines.launchimport uz.lutfullayevmuhammad.lesson63.core.helper.ResultWrapperimport uz.lutfullayevmuhammad.lesson63.core.models.detailes.DetailesResponseimport uz.lutfullayevmuhammad.lesson63.core.models.error.UniversalErrorimport uz.lutfullayevmuhammad.lesson63.core.repo.HomeRepoimport javax.inject.Injectclass DetailsViewModel @Inject constructor(    var repo: HomeRepo) : ViewModel() {    val detailsLiveData = MutableLiveData<DetailesResponse>()    val errorLiveData = MutableLiveData<UniversalError?>()    val networkErrorLiveData = MutableLiveData<Nothing>()    fun detailsMeal(id: String) {        CoroutineScope(Dispatchers.IO).launch {            val response = repo.detailsData(id)            when (response) {                is ResultWrapper.Success -> {                    detailsLiveData.postValue(response.response!!)                }                is ResultWrapper.ErrorResponse -> {                    errorLiveData.postValue(response.errorResponse)                }                is ResultWrapper.NetworkError -> {                    networkErrorLiveData.postValue(null)                }            }        }    }    override fun onCleared() {        Log.d("MainViewModelTAG", "onCleared: ")        super.onCleared()    }}